import React, {useState, useEffect} from 'react';
// import { firebaseConfig } from '../../context/firebaseConfig';
// import { initializeApp } from 'firebase/app';
import { getDatabase, ref, onValue, get, child, push, update } from "firebase/database";
import { app } from "../../firebase";

import {
  DesktopOutlined,
  FileOutlined,
  GatewayOutlined,
  LoginOutlined,
  UserOutlined
} from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { Breadcrumb, Layout, Menu, theme, Image } from 'antd';

import Calculadora from "../Calculadura";
import Galeria from "../Galeria";
import Datos from "./Datos";

import { useAuth } from "../../context/AuthContext";

const { Header, Content, Footer, Sider } = Layout;

type MenuItem = Required<MenuProps>['items'][number];


// export interface
export interface Data {
  name: string, 
  username: string, 
  age: number, 
  picture: string 
}

function App() {
  const { logout, user } = useAuth();
  const emailUser = user.email.replace(/[^\w\s]|_/g, "");
  const [data, setData] = useState({} as Data);
  // const app = initializeApp(firebaseConfig)
  const database = getDatabase(app);
  const starCountRef = ref(database, emailUser);

  function getItem(
    label: React.ReactNode,
    key: React.Key,
    icon?: React.ReactNode,
    children?: MenuItem[],
  ): MenuItem {
    return {
      key,
      icon,
      children,
      label,
    } as MenuItem;
  }

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.log(error);
    }
  };

  const items: MenuItem[] = [
    getItem(<div>{user.email}</div>, '1', <UserOutlined />),
    getItem('Calculadora', '2', <DesktopOutlined />),
    getItem('Galeria', '3', <GatewayOutlined />),
    getItem(<p onClick={handleLogout}>logout</p>, '4', <LoginOutlined />),
  ];
  
  const onClick: MenuProps['onClick'] = (e: {key: string}) => {
    switch (e.key) {
      case '1':
        setSection('principal');
        break;
      case '2':
        setSection('calculadora');
        break;
      case '3':
        setSection('galeria');
        break;
      default:
        setSection('principal');
    }
  };

  const [collapsed, setCollapsed] = useState(false);
  const [section, setSection] = useState("principal");

  get(starCountRef).then((snapshot) => {  
    if(snapshot.val()){
      if(!data.name){
        setData(snapshot.val());
      }
    }
  });

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={collapsed} onCollapse={(value: boolean) => setCollapsed(value)} >
        <div className="demo-logo-vertical" />
        <Image
          src={data && data.picture}
          height="100px"
          style={{display: "flex",padding: "10px", objectFit: "contain", maxWidth: "100%", maxHeight: "100%"}}
          fallback="data:image/png;base64,"
        />
        <Menu onClick={onClick} theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} />
      </Sider>
      <Layout>
        <Header style={{ padding: 0, alignItems: "end" }} >
        </Header>
        <Content style={{ margin: '0 16px' }}>
          <Breadcrumb style={{ margin: '16px 0', fontSize: "35px", fontWeight: "semi-bold", borderBottom: "1px solid #DDE6ED"}}>
            <Breadcrumb.Item>{ section }</Breadcrumb.Item> 
          </Breadcrumb> 
          {section === "principal" ? <Datos/> : (section === "calculadora"? <Calculadora/> : <Galeria/>)  }
        </Content>
        <Footer style={{ textAlign: 'center' }}>Prueba TÃ©cnica - Desarrollador React Eduardo</Footer>
      </Layout>
    </Layout>
  );
}

export default App;